from collections import Counter


class Solution:

    def characterReplacement(self, s: str, k: int) -> int:
        l, r, n = 0, 1, len(s)
        max_len = 0
        char_counter = Counter(s[0])
        max_frequency = 1
        while l < n and r <= n + 1:
            window_size = r - l
            diff = window_size - max_frequency
            if diff <= k:
                max_len = max(max_len, window_size)
                if r < n:
                    char_counter[s[r]] += 1
                    max_frequency = max(max_frequency, char_counter[s[r]])
                    r += 1
                else:
                    char_counter[s[l]] -= 1
                    l += 1
            else:
                char_counter[s[l]] -= 1
                l += 1
        return max_len


print(Solution().characterReplacement(s="ABAB", k=2))
print(Solution().characterReplacement(s="AABABBA", k=1))
print(Solution().characterReplacement(s="AABABBA", k=2))
print(Solution().characterReplacement(
    s=
    "",
    k=3371))
